text:
.tc-error-form {
  font-family: sans-serif;
  color: #fff;
  z-index: 20000;
  position: fixed;
  background-color: rgb(255, 75, 75);
  border: 8px solid rgb(255, 0, 0);
  border-radius: 8px;
  width: 50%;
  margin-left: 25%;
  margin-top: 4em;
  padding: 0 2em 1em 2em;
}

.tc-error-form h1 {
  text-align: center;
}

.tc-error-prompt {
  text-align: center;
  color: #000;
}

.tc-error-message {
  overflow: auto;
  max-height: 40em;
  padding-right: 1em;
  margin: 1em 0;
  white-space: pre-line;
}

.tc-password-wrapper {
  font-family: sans-serif;
  z-index: 20000;
  position: fixed;
  text-align: center;
  width: 200px;
  top: 4em;
  left: 50%;
  margin-left: -144px; /* - width/2 - paddingHorz/2 - border */
  padding: 16px 16px 16px 16px;
  border-radius: 8px;
}

.tc-password-wrapper {
  color: #000;
  text-shadow: 0 1px 0 rgba(255, 255, 255, 0.5);
  background-color: rgb(197, 235, 183);
  border: 8px solid rgb(164, 197, 152);
}

.tc-password-wrapper form {
  text-align: left;
}

.tc-password-wrapper h1 {
  font-size: 16px;
  line-height: 20px;
  padding-bottom: 16px;
}

.tc-password-wrapper input {
  width: 100%;
}
